    function getFinish(data) {
        var events,
            i,
            finish;

        if(data.task) {
            events = data.task.taskEvents;
            // Get the last set finish time
            for(i = events.length - 1; i >= 0; i--) {
                if(events[i].schedule_finish) {
                    finish = events[i].schedule_finish;
                    break;
                }
            }
        }
        return finish;
    }

